import React, { useState } from "react";

import "./ExpensesFilter.css";

const ExpensesFilter = () => {
  //
  const [original, newOriginal] = useState("");

  const yearChoose = (event) => {
    newOriginal(event.target.value);
  };

  function yearSelected() {
    console.log(original);
  }

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select onChange={yearSelected}>
          <option value="2022" type="submit" onChange={yearChoose}>
            2022
          </option>
          <option value="2021" type="submit" onChange={yearChoose}>
            2021
          </option>
          <option value="2020" type="submit" onChange={yearChoose}>
            2020
          </option>
          <option value="2019" type="submit" onChange={yearChoose}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
